mapper.select:
SELECT F.RNUM , F.EMAIL , F.NICKNAME , F.PASSWORD , F.PHONE , F.IS_JANDI , F.WARNING , F.IS_BLACK , F.ENROLL_DATE , F.IS_EXIT , F.NAME FROM (SELECT ROWNUM RNUM , D.EMAIL , D.NICKNAME , D.PASSWORD , D.PHONE , D.IS_JANDI , D.WARNING , D.IS_BLACK , D.ENROLL_DATE , D.IS_EXIT , D.NAME FROM (SELECT A.EMAIL , A.NICKNAME , A.PASSWORD , A.PHONE , A.IS_JANDI , A.WARNING , A.IS_BLACK , A.ENROLL_DATE , A.IS_EXIT , A.NAME FROM SSACK_TBL A
A.NICKNAME LIKE '%' || #{ searchValue } || '%'
AND A.EMAIL LIKE '%' || #{ searchValue } || '%'
AND A.NAME LIKE '%' || #{ searchValue } || '%'
ORDER BY A.ENROLL_DATE DESC ) D
WHERE ROWNUM <= #{ endRow }
) F WHERE F.RNUM >= #{ startRow }

SELECT F.RNUM , F.NICKNAME , F.CAREER , F.INTRO , F.PROFILE_PATH , F.ENROLL_DATE , F.ACCOUNT , F.BANK , F.ACC_NAME , F.EMAIL FROM (SELECT ROWNUM RNUM , D.NICKNAME , D.CAREER , D.INTRO , D.PROFILE_PATH , D.ENROLL_DATE , D.ACCOUNT , D.BANK , D.ACC_NAME , D.EMAIL FROM (SELECT A.NICKNAME , A.CAREER , A.INTRO , A.PROFILE_PATH , A.ENROLL_DATE , A.ACCOUNT , A.BANK , A.ACC_NAME , A.EMAIL FROM JANDI_TBL A
A.NICKNAME LIKE '%' || #{ searchValue } || '%'
AND A.WARNING LIKE '%' || #{ searchValue } || '%'
AND A.ACC_NAME LIKE '%' || #{ searchValue } || '%'
ORDER BY A.ENROLL_DATE DESC ) D
WHERE ROWNUM <= #{ endRow }
) F WHERE F.RNUM >= #{ startRow }

SELECT C.RNUM , C.POST_CODE , C.TITLE , C.CONTENTS , C.WRITE_DATE , C.IS_DELETED , C.MNG_NO FROM (SELECT ROWNUM RNUM , B.POST_CODE , B.TITLE , B.CONTENTS , B.WRITE_DATE , B.IS_DELETED , B.MNG_NO FROM (SELECT A.POST_CODE , A.TITLE , A.CONTENTS , A.WRITE_DATE , A.IS_DELETED , A.MNG_NO FROM FAQ_TBL A
A.TITLE LIKE '%' || #{ searchValue } || '%'
ORDER BY A.POST_CODE DESC ) B
WHERE ROWNUM <= #{ endRow }
) C WHERE C.RNUM >= #{ startRow }

