mapper.select:
SELECT F.RNUM , F.REP_CODE , F.EMAIL , F.CONTENTS , F.REP_DATE , F.REP_EMAIL , F.REP_TYPE_CODE , F.ORG_IMG_PATH , F.REP_YN , F.REP_TYPE FROM (SELECT ROWNUM RNUM , D.REP_CODE , D.EMAIL , D.CONTENTS , D.REP_DATE , D.REP_EMAIL , D.REP_TYPE_CODE , D.ORG_IMG_PATH , D.REP_YN , D.REP_TYPE FROM (SELECT R.REP_CODE , R.EMAIL , R.CONTENTS , R.REP_DATE , R.REP_EMAIL , R.REP_TYPE_CODE , R.ORG_IMG_PATH , R.REP_YN , S.REP_TYPE FROM REPORT_MEMBER_TBL R JOIN REPORT_STATEMENT_TBL S ON(S.REP_TYPE_CODE = R.REP_TYPE_CODE)
R.REP_EMAIL LIKE '%' || #{ searchValue } || '%'
AND R.CONTENTS LIKE '%' || #{ searchValue } || '%'
AND R.REP_YN LIKE '%' || #{ searchValue } || '%'
ORDER BY R.REP_DATE DESC ) D
WHERE ROWNUM <= #{ endRow }
) F WHERE F.RNUM >= #{ startRow }

SELECT F.RNUM , F.REP_CODE , F.EMAIL , F.REP_CONTENTS , F.CLASS_CODE , F.REP_DATE , F.REP_TYPE_CODE , F.REP_YN , F.REP_TYPE FROM (SELECT ROWNUM RNUM , D.REP_CODE , D.EMAIL , D.REP_CONTENTS , D.CLASS_CODE , D.REP_DATE , D.REP_TYPE_CODE , D.REP_YN , D.REP_TYPE FROM (SELECT R.REP_CODE , R.EMAIL , R.REP_CONTENTS , R.CLASS_CODE , R.REP_DATE , R.REP_TYPE_CODE , R.REP_YN , S.REP_TYPE FROM REPORT_CLASS_TBL R JOIN REPORT_STATEMENT_TBL S ON(S.REP_TYPE_CODE = R.REP_TYPE_CODE)
R.REP_EMAIL LIKE '%' || #{ searchValue } || '%'
AND R.REP_CONTENTS LIKE '%' || #{ searchValue } || '%'
AND R.REP_YN LIKE '%' || #{ searchValue } || '%'
ORDER BY R.REP_DATE DESC ) D
WHERE ROWNUM <= #{ endRow }
) F WHERE F.RNUM >= #{ startRow }
