mapper.select:
SELECT
       A.EMAIL
  FROM SSACK_TBL A
 WHERE A.NAME = #{ name }
   AND A.PHONE = #{ phone }

        SELECT
		       COUNT(*)
		  FROM SSACK_TBL A
		 WHERE A.NAME= #{ name }
           AND A.EMAIL= #{ email }

mapper.update:
        UPDATE 
		       SSACK_TBL A
		   SET A.PASSWORD = #{ pwd }
		 WHERE A.EMAIL = #{ email }

        UPDATE EMAIL_CHECK_TBL A 
		   SET A.EMAIL = #{ email }
		 WHERE A.CHECK_NUMBER = #{ number }

        UPDATE EMAIL_CHECK_TBL A
		   SET A.CHECK_NUMBER = #{ number }
		 WHERE A.EMAIL = #{ email }

