mapper.update:
    UPDATE 
	       SSACK_TBL A
	   SET A.NICKNAME = #{ nickName }
	     , A.PHONE = #{ phone }
	 WHERE A.EMAIL = #{ email }

    UPDATE SSACK_TBL A
	   SET A.PASSWORD = #{ setPwd }
	 WHERE A.EMAIL = #{ email }

    UPDATE SSACK_TBL A
	   SET A.IS_EXIT = 'Y'
	 WHERE A.EMAIL = #{ email }

mapper.select:
SELECT F.RNUM , F.EMAIL , F.CLASS_CODE , F.STATUS , F.PAY_CODE , F.CLASS_PURC_CODE , F.END_DATE , F.CATEGORY_NAME , F.TITLE , F.NICKNAME , F.PAY_DATE FROM (SELECT ROWNUM RNUM , D.EMAIL , D.CLASS_CODE , D.STATUS , D.PAY_CODE , D.CLASS_PURC_CODE , D.END_DATE , D.CATEGORY_NAME , D.TITLE , D.NICKNAME , D.PAY_DATE FROM (SELECT A.EMAIL , A.CLASS_CODE , A.STATUS , A.PAY_CODE , A.CLASS_PURC_CODE , A.END_DATE , C.CATEGORY_NAME , L.TITLE , J.NICKNAME , E.PAY_DATE FROM PURCHASE_CLASS_TBL A JOIN CLASS_TBL L ON (L.CLASS_CODE = A.CLASS_CODE ) JOIN CATEGORY_TBL C ON (C.CATEGORY_CODE = L.CATEGORY_CODE) JOIN JANDI_TBL J ON (L.EMAIL = J.EMAIL ) JOIN PAYMENT_TBL E ON (A.PAY_CODE = E.PAY_CODE) WHERE A.STATUS = 'N' AND A.EMAIL = #{ email }
C.CATEGORY_NAME LIKE '%' || #{ selectCriteria.searchValue } || '%'
L.TITLE LIKE '%' || #{ selectCriteria.searchValue } || '%'
J.NICKNAME '%' || #{ selectCriteria.searchValue } || '%'
ORDER BY A.END_DATE DESC ) D
WHERE ROWNUM <= #{ selectCriteria.endRow }
) F WHERE F.RNUM >= #{ selectCriteria.startRow }

SELECT V2.RNUM , V2.* FROM (SELECT ROWNUM RNUM , V.* FROM (SELECT A.CLASS_CODE , B.TITLE , B.FILE_PATH , C.CATEGORY_NAME , D.NICKNAME FROM LIKED_CLASS_TBL A JOIN CLASS_TBL B ON (A.CLASS_CODE = B.CLASS_CODE) JOIN CATEGORY_TBL C ON (B.CATEGORY_CODE = C.CATEGORY_CODE) JOIN JANDI_TBL D ON (B.EMAIL = D.EMAIL)
A.EMAIL = #{ email }
AND C.CATEGORY_NAME LIKE '%' || #{ selectCriteria.searchValue } || '%'
AND B.TITLE LIKE '%' || #{ selectCriteria.searchValue } || '%'
AND D.NICKNAME LIKE '%' || #{ selectCriteria.searchValue } || '%'
)V )V2 WHERE V2.RNUM BETWEEN #{ selectCriteria.startRow } AND #{ selectCriteria.endRow }
