mapper.select:
SELECT F.RNUM , F.EMAIL , F.CLASS_CODE , F.STATUS , F.PAY_CODE , F.CLASS_PURC_CODE , F.END_DATE , F.CATEGORY_NAME , F.TITLE , F.NICKNAME , F.PAY_DATE , F.REVIEW_CODE , F.IS_DELETED FROM (SELECT ROWNUM RNUM , D.EMAIL , D.CLASS_CODE , D.STATUS , D.PAY_CODE , D.CLASS_PURC_CODE , D.END_DATE , D.CATEGORY_NAME , D.TITLE , D.NICKNAME , D.PAY_DATE , D.REVIEW_CODE , D.IS_DELETED FROM (SELECT A.EMAIL , A.CLASS_CODE , A.STATUS , A.PAY_CODE , A.CLASS_PURC_CODE , A.END_DATE , C.CATEGORY_NAME , L.TITLE , J.NICKNAME , E.PAY_DATE , T.REVIEW_CODE , T.IS_DELETED FROM PURCHASE_CLASS_TBL A LEFT JOIN CLASS_REVIEW_TBL T ON(A.CLASS_PURC_CODE = T.CLASS_PURC_CODE) JOIN CLASS_TBL L ON (L.CLASS_CODE = A.CLASS_CODE ) JOIN CATEGORY_TBL C ON (C.CATEGORY_CODE = L.CATEGORY_CODE) JOIN JANDI_TBL J ON (L.EMAIL = J.EMAIL ) JOIN PAYMENT_TBL E ON (A.PAY_CODE = E.PAY_CODE)
A.STATUS = 'N' AND A.EMAIL = #{ email }
AND C.CATEGORY_NAME LIKE '%' || #{ selectCriteria.searchValue } || '%'
AND L.TITLE LIKE '%' || #{ selectCriteria.searchValue } || '%'
AND J.NICKNAME LIKE '%' || #{ selectCriteria.searchValue } || '%'
ORDER BY A.END_DATE DESC ) D
WHERE ROWNUM <= #{ selectCriteria.endRow }
) F WHERE F.RNUM >= #{ selectCriteria.startRow }

