  , #{ classCode }
  , #{ email }
  , #{ classPurcCode }
)

mapper.select:
SELECT V2.RNUM , V2.* FROM (SELECT ROWNUM RNUM , V.* FROM (SELECT A.CLASS_CODE , B.TITLE , B.FILE_PATH , C.CATEGORY_NAME , B.IS_DELETED , D.NICKNAME FROM LIKED_CLASS_TBL A JOIN CLASS_TBL B ON (A.CLASS_CODE = B.CLASS_CODE) JOIN CATEGORY_TBL C ON (B.CATEGORY_CODE = C.CATEGORY_CODE) JOIN JANDI_TBL D ON (B.EMAIL = D.EMAIL)
A.EMAIL = #{ email }
AND C.CATEGORY_NAME LIKE '%' || #{ selectCriteria.searchValue } || '%'
AND B.TITLE LIKE '%' || #{ selectCriteria.searchValue } || '%'
AND D.NICKNAME LIKE '%' || #{ selectCriteria.searchValue } || '%'
)V )V2 WHERE V2.RNUM BETWEEN #{ selectCriteria.startRow } AND #{ selectCriteria.endRow }

