mapper.select:
SELECT V2.RNUM , V2.* FROM ( SELECT ROWNUM RNUM , V.* FROM ( SELECT A.CLASS_CODE , B.TITLE , B.FILE_PATH , B.IS_DELETED , C.NICKNAME , D.PAY_DATE , D.PAY FROM PURCHASE_CLASS_TBL A JOIN CLASS_TBL B ON(A.CLASS_CODE = B.CLASS_CODE) JOIN JANDI_TBL C ON(B.EMAIL = C.EMAIL) JOIN PAYMENT_TBL D ON(A.PAY_CODE = D.PAY_CODE)
A.EMAIL = #{ email }
AND B.TITLE LIKE '%' || #{ searchValue } || '%'
AND C.NICKNAME LIKE '%' || #{ searchValue } || '%'
ORDER BY D.PAY_DATE ASC
ORDER BY D.PAY DESC
ORDER BY D.PAY_DATE DESC
ORDER BY D.PAY ASC
)V )V2 WHERE V2.RNUM BETWEEN #{ selectCriteria.startRow } AND #{ selectCriteria.endRow }

SELECT F.RNUM , F.EMAIL , F.REVIEW_CODE , F.WRITE_DATE , F.STAR , F.CONTENTS , F.IS_DELETED , F.CLASS_CODE , F.CATEGORY_NAME , F.TITLE , F.NICKNAME , F.ORG_FILE_PATH FROM (SELECT ROWNUM RNUM , D.EMAIL , D.REVIEW_CODE , D.WRITE_DATE , D.STAR , D.CONTENTS , D.IS_DELETED , D.CLASS_CODE , D.CATEGORY_NAME , D.TITLE , D.NICKNAME , D.ORG_FILE_PATH FROM (SELECT R.EMAIL , R.REVIEW_CODE , R.WRITE_DATE , R.STAR , R.CONTENTS , R.IS_DELETED , R.CLASS_CODE , C.CATEGORY_NAME , L.TITLE , J.NICKNAME , L.ORG_FILE_PATH FROM CLASS_REVIEW_TBL R JOIN CLASS_TBL L ON (L.CLASS_CODE = R.CLASS_CODE ) JOIN CATEGORY_TBL C ON (C.CATEGORY_CODE = L.CATEGORY_CODE) JOIN JANDI_TBL J ON (L.EMAIL = J.EMAIL ) WHERE R.EMAIL = #{ email } AND R.IS_DELETED = 'N' ORDER BY R.WRITE_DATE DESC ) D
WHERE ROWNUM <= #{ selectCriteria.endRow }
) F WHERE F.RNUM >= #{ selectCriteria.startRow }

